function getStatus(t){return'<b><span class="'+t+'">'+t.toUpperCase()+"</span></b>"}function Status(t){document.getElementById("mainStatus").innerHTML=getStatus(t.status)}function Messages(t){if(0==t.length)var s="<i>No new messages</i>";else{s='<table width="100%">';for(var e=0;e<t.length;e++){var a=new Date(t[e].created_on).toString();a='<span class="date">'+a.substring(4,15)+" at "+a.substring(16,24)+"</span>",s+='<tr><td class="'+t[e].status.toLowerCase()+'t size"></td><td><span>'+t[e].body+"</span><br />"+a+"</tr>"}s+="</table>"}document.getElementById("messages").innerHTML=s}
